
import re

pattern = "ATAT"
text = "GATATATGCATATACTT"

pattern = "CATCTCCCA"
text = ""


def find_start(text, pattern):
    pos_ahead_pattern = "(?=" + pattern + ")"
    locations = [m.start() for m in re.finditer(pos_ahead_pattern, text)]
    return locations

res = find_start(text, pattern)
new_res=[str(i) for i in res]
str1 = ' '.join(new_res)

with open('../Data/Vibrio_cholerae.txt') as f:
    contents = f.read()

pattern_new = "CTTGATCAT"


res = find_start(contents, pattern_new)
new_res=[str(i) for i in res]
str1 = ' '.join(new_res)

with open('answer.txt', 'w') as f:
    for item in str1:
        f.write(item)